import React, { useState } from 'react';
import api from '../services/api';

const CreateCustomerModal = ({ onClose, onSuccess }) => {
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    phone: '',
    email: '',
    contributionAmount: '',
    contributionFrequency: 'monthly',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/customers/create', form);
      onSuccess();
    } catch (error) {
      setMessage('Failed to create customer.');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded shadow-md w-96">
        <h2 className="text-2xl mb-4">Create Customer</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            value={form.firstName}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
            required
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={form.lastName}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
            required
          />
          <input
            type="date"
            name="dateOfBirth"
            value={form.dateOfBirth}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
            required
          />
          <input
            type="text"
            name="phone"
            placeholder="Phone"
            value={form.phone}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email (optional)"
            value={form.email}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
          />
          <input
            type="number"
            name="contributionAmount"
            placeholder="Contribution Amount"
            value={form.contributionAmount}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
          />
          <select
            name="contributionFrequency"
            value={form.contributionFrequency}
            onChange={handleChange}
            className="w-full p-2 mb-2 border"
          >
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
            <option value="yearly">Yearly</option>
          </select>
          <div className="flex justify-end">
            <button type="button" onClick={onClose} className="mr-2 px-4 py-2">Cancel</button>
            <button type="submit" className="bg-blue-500 text-white px-4 py-2">Create</button>
          </div>
        </form>
        {message && <p className="mt-2 text-center">{message}</p>}
      </div>
    </div>
  );
};

export default CreateCustomerModal;
