import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import CreateCustomerModal from '../components/CreateCustomerModal';
import AddPaymentModal from '../components/AddPaymentModal';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const res = await api.get('/customers/list');
      setCustomers(res.data);
    } catch (error) {
      if (error.response?.status === 401) {
        navigate('/login');
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
      </div>
      <div className="mb-4">
        <button onClick={() => setShowCreateModal(true)} className="bg-green-500 text-white px-4 py-2 rounded mr-2">Create Customer</button>
      </div>
      <div>
        <h2 className="text-2xl mb-4">Customers</h2>
        {customers.length === 0 ? (
          <p>No customers yet.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {customers.map((customer) => (
              <div key={customer._id} className="bg-white p-4 rounded shadow">
                <h3 className="text-xl">{customer.firstName} {customer.lastName}</h3>
                <p>Phone: {customer.phone}</p>
                <p>Email: {customer.email || 'N/A'}</p>
                <p>Balance: {customer.balance}</p>
                <p>Frequency: {customer.contributionFrequency}</p>
                <button
                  onClick={() => {
                    setSelectedCustomer(customer);
                    setShowPaymentModal(true);
                  }}
                  className="bg-blue-500 text-white px-4 py-2 rounded mt-2"
                >
                  Add Payment
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      {showCreateModal && (
        <CreateCustomerModal
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            fetchCustomers();
          }}
        />
      )}
      {showPaymentModal && selectedCustomer && (
        <AddPaymentModal
          customer={selectedCustomer}
          onClose={() => setShowPaymentModal(false)}
          onSuccess={() => {
            setShowPaymentModal(false);
            fetchCustomers();
          }}
        />
      )}
    </div>
  );
};

export default Dashboard;
