import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../services/api';

const AddPayment = () => {
  const { customerId } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [amount, setAmount] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [notifyType, setNotifyType] = useState('none');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [paidDays, setPaidDays] = useState(new Set());
  const [cycleStartDay, setCycleStartDay] = useState(null);

  React.useEffect(() => {
    fetchCustomer();
  }, [customerId]);

  const fetchCustomer = async () => {
    try {
      // Fetch customer details
      const customersRes = await api.get('/customers/list');
      const foundCustomer = customersRes.data.find(c => c._id === customerId);
      setCustomer(foundCustomer);
      setAmount(foundCustomer?.contributionAmount || '');

      // Fetch existing payments to mark paid days
      const paymentsRes = await api.get('/payments/list');
      const customerPayments = paymentsRes.data.filter(payment =>
        payment.customerId._id === customerId
      );

      // For daily customers, determine the cycle start day
      let startDay = null;
      if (foundCustomer.contributionFrequency === 'daily' && customerPayments.length > 0) {
        // Find the earliest payment date
        const earliestPayment = customerPayments.reduce((earliest, payment) => {
          const paymentDate = new Date(payment.createdAt);
          return !earliest || paymentDate < earliest ? paymentDate : earliest;
        }, null);
        startDay = earliestPayment ? earliestPayment.getDate() : null;
      }
      setCycleStartDay(startDay);

      // Extract day numbers from payment dates
      const paidDaysSet = new Set();
      customerPayments.forEach(payment => {
        const paymentDate = new Date(payment.createdAt);
        const day = paymentDate.getDate();
        paidDaysSet.add(day);
      });
      setPaidDays(paidDaysSet);

    } catch (error) {
      console.error('Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedDate) {
      setMessage('Please select a payment date');
      return;
    }

    try {
      const paymentDate = `${new Date().getFullYear()}-${String(new Date().getMonth() + 1).padStart(2, '0')}-${String(selectedDate).padStart(2, '0')}`;
      await api.post('/payments/add', {
        customerId,
        amount: parseFloat(amount),
        paymentDate,
        notifyType,
      });
      navigate('/dashboard');
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to add payment.');
    }
  };

  const getNextAvailableDay = () => {
    if (customer?.contributionFrequency === 'daily') {
      for (let day = 1; day <= 31; day++) {
        if (!paidDays.has(day)) {
          return day;
        }
      }
    }
    return null;
  };

  const getNextAvailableDate = () => {
    if (!paidDays.size) {
      return 1; // First payment can be any day
    }

    if (customer?.contributionFrequency === 'weekly') {
      // For weekly, next payment must be at least 7 days after the last payment
      const lastPaidDay = Math.max(...Array.from(paidDays));
      return lastPaidDay + 7;
    } else if (customer?.contributionFrequency === 'monthly') {
      // For monthly, next payment must be at least 30 days after the last payment
      const lastPaidDay = Math.max(...Array.from(paidDays));
      return lastPaidDay + 30;
    } else if (customer?.contributionFrequency === 'yearly') {
      // For yearly, next payment must be at least 365 days after the last payment
      const lastPaidDay = Math.max(...Array.from(paidDays));
      return lastPaidDay + 365;
    }

    return null;
  };

  const getWeekDays = (weekNum) => {
    const startDay = (weekNum - 1) * 7 + 1;
    const endDay = Math.min(weekNum * 7, 31);
    const days = [];
    for (let day = startDay; day <= endDay; day++) {
      days.push(day);
    }
    return days;
  };

  const getIntervalDays = () => {
    switch (customer?.contributionFrequency) {
      case 'weekly': return 7;
      case 'monthly': return 30;
      case 'yearly': return 365;
      default: return 1;
    }
  };

  const getWeekDateRange = (weekNum) => {
    const currentYear = new Date().getFullYear();
    const currentMonth = new Date().getMonth();
    const weekDays = getWeekDays(weekNum);
    const startDate = new Date(currentYear, currentMonth, weekDays[0]);
    const endDate = new Date(currentYear, currentMonth, weekDays[weekDays.length - 1]);

    return `${startDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - ${endDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}`;
  };

  const renderCalendar = () => {
    return renderDailyCalendar();
  };

  const renderDailyCalendar = () => {
    const currentDate = new Date();
    const currentYear = currentDate.getFullYear();
    const currentMonth = currentDate.getMonth();
    const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    const nextAvailableDay = getNextAvailableDate();
    const days = [];

    for (let day = 1; day <= daysInMonth; day++) {
      const isPaid = paidDays.has(day);
      const isSelected = selectedDate === day;
      const isAvailable = day === nextAvailableDay || (!paidDays.size && !isPaid); // First payment can be any day
      const isBlocked = !isPaid && !isAvailable;

      // Create actual date for this day
      const dayDate = new Date(currentYear, currentMonth, day);
      const isToday = dayDate.toDateString() === currentDate.toDateString();

      days.push(
        <button
          key={day}
          onClick={() => isAvailable && setSelectedDate(day)}
          disabled={isPaid || isBlocked}
          className={`w-12 h-12 rounded-lg font-semibold transition-all duration-200 relative ${
            isPaid
              ? 'bg-green-500 text-white shadow-lg cursor-not-allowed opacity-75'
              : isSelected
              ? 'bg-blue-500 text-white shadow-lg transform scale-110'
              : isAvailable
              ? 'bg-white bg-opacity-20 text-white hover:bg-opacity-30 hover:scale-105 cursor-pointer border-2 border-yellow-400'
              : 'bg-gray-500 bg-opacity-20 text-gray-400 cursor-not-allowed opacity-50'
          }`}
        >
          {day}
          {isToday && <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></span>}
          {isPaid && <span className="text-xs">✓</span>}
          {isAvailable && !isPaid && <span className="text-xs text-yellow-400">→</span>}
        </button>
      );
    }

    return days;
  };


  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-400 via-indigo-500 to-purple-600 flex items-center justify-center">
        <div className="text-white text-xl">Loading...</div>
      </div>
    );
  }

  if (!customer) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-400 via-indigo-500 to-purple-600 flex items-center justify-center">
        <div className="text-center">
          <div className="text-white text-xl mb-4">Customer not found</div>
          <button
            onClick={() => navigate('/dashboard')}
            className="bg-white bg-opacity-20 text-white px-6 py-3 rounded-xl hover:bg-opacity-30 transition-all duration-200"
          >
            Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-400 via-indigo-500 to-purple-600">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-cyan-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse animation-delay-2000"></div>
        <div className="absolute top-40 left-40 w-80 h-80 bg-indigo-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse animation-delay-4000"></div>
      </div>

      <div className="relative z-10">
        {/* Header */}
        <header className="bg-white bg-opacity-10 backdrop-blur-lg border-b border-white border-opacity-20">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center py-6">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-500 rounded-2xl flex items-center justify-center shadow-2xl">
                  <span className="text-white font-bold text-2xl">O</span>
                </div>
                <h1 className="ml-4 text-2xl font-bold text-white">Add Payment</h1>
              </div>
              <button
                onClick={() => navigate('/dashboard')}
                className="bg-white bg-opacity-20 text-white px-6 py-2 rounded-xl hover:bg-opacity-30 backdrop-blur-sm border border-white border-opacity-30 transition-all duration-200"
              >
                Back to Dashboard
              </button>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Customer Info */}
          <div className="bg-white bg-opacity-10 backdrop-blur-lg rounded-2xl shadow-2xl border border-white border-opacity-20 p-8 mb-8">
            <div className="flex items-center mb-6">
              <div className="w-16 h-16 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full flex items-center justify-center mr-6">
                <span className="text-white font-bold text-2xl">
                  {customer.firstName[0]}{customer.lastName[0]}
                </span>
              </div>
              <div>
                <h2 className="text-3xl font-bold text-white">
                  {customer.firstName} {customer.lastName}
                </h2>
                <p className="text-white text-opacity-80">{customer.phone}</p>
                <p className="text-white text-opacity-60">
                  Contribution: ₦{customer.contributionAmount} ({customer.contributionFrequency})
                </p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Calendar Section */}
            <div className="bg-white bg-opacity-10 backdrop-blur-lg rounded-2xl shadow-2xl border border-white border-opacity-20 p-8">
              <h3 className="text-2xl font-bold text-white mb-6">Select Payment Date</h3>

              {/* Legend */}
              <div className="flex flex-wrap gap-4 mb-6">
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-green-500 rounded mr-2"></div>
                  <span className="text-white text-sm">Completed ✓</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-blue-500 rounded mr-2"></div>
                  <span className="text-white text-sm">Selected</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-yellow-400 border-2 border-yellow-400 rounded mr-2"></div>
                  <span className="text-white text-sm">Next Available →</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-gray-500 bg-opacity-20 rounded mr-2"></div>
                  <span className="text-white text-sm">Locked</span>
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-2 gap-4 mb-6">
                <div className="bg-white bg-opacity-20 rounded-xl p-4">
                  <p className="text-white text-opacity-80 text-sm">Payments Made</p>
                  <p className="text-white font-bold text-xl">{paidDays.size}</p>
                </div>
                <div className="bg-white bg-opacity-20 rounded-xl p-4">
                  <p className="text-white text-opacity-80 text-sm">Next Payment</p>
                  <p className="text-white font-bold text-xl">
                    {getNextAvailableDate() ? `Day ${getNextAvailableDate()}` : 'Any day'}
                  </p>
                </div>
              </div>

              <>
                <div className="grid grid-cols-7 gap-2 mb-4">
                  {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                    <div key={day} className="text-center text-white text-opacity-60 font-medium py-2">
                      {day}
                    </div>
                  ))}
                </div>
                <div className="grid grid-cols-7 gap-2">
                  {renderCalendar()}
                </div>
              </>
              {selectedDate && (
                <div className="mt-6 p-4 bg-white bg-opacity-20 rounded-xl">
                  <p className="text-white font-semibold">
                    Selected Date: {new Date(new Date().getFullYear(), new Date().getMonth(), selectedDate).toLocaleDateString('en-US', {
                      weekday: 'long',
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })}
                  </p>
                  <p className="text-white text-opacity-80 text-sm mt-1">
                    Next payment due: {new Date(new Date().getFullYear(), new Date().getMonth(), selectedDate + getIntervalDays()).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      year: 'numeric'
                    })}
                  </p>
                </div>
              )}
            </div>

            {/* Payment Form */}
            <div className="bg-white bg-opacity-10 backdrop-blur-lg rounded-2xl shadow-2xl border border-white border-opacity-20 p-8">
              <h3 className="text-2xl font-bold text-white mb-6">Payment Details</h3>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                  <label className="block text-white text-sm font-medium mb-2">Payment Amount</label>
                  <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    className="w-full px-4 py-3 bg-white bg-opacity-20 border border-white border-opacity-30 rounded-xl text-white placeholder-white placeholder-opacity-70 focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:border-transparent transition-all duration-200"
                    required
                    min="1"
                  />
                </div>

                <div>
                  <label className="block text-white text-sm font-medium mb-2">Notification Method</label>
                  <select
                    value={notifyType}
                    onChange={(e) => setNotifyType(e.target.value)}
                    className="w-full px-4 py-3 bg-white bg-opacity-20 border border-white border-opacity-30 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:border-transparent transition-all duration-200"
                  >
                    <option value="none" className="text-gray-900">No Notification</option>
                    <option value="sms" className="text-gray-900">Send SMS</option>
                    <option value="email" className="text-gray-900">Send Email</option>
                  </select>
                </div>

                <button
                  type="submit"
                  disabled={!selectedDate}
                  className="w-full bg-gradient-to-r from-green-400 to-blue-500 text-white py-4 px-6 rounded-2xl font-semibold hover:from-green-500 hover:to-blue-600 transform hover:scale-105 transition-all duration-200 shadow-xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
                >
                  💰 Record Payment
                </button>
              </form>

              {message && (
                <div className="mt-4 p-3 rounded-xl text-center bg-red-500 bg-opacity-20 text-red-100 border border-red-500 border-opacity-30">
                  {message}
                </div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AddPayment;
